import axios from 'axios';

// 获取电影评论列表
export function getComments(movieId) {
    return axios.get(`/api/movies/${movieId}/comments`).then(res => res.data);
}

// 添加电影评论
export function addComment(movieId, content) {
    return axios.post(`/api/movies/${movieId}/comments`, { content }).then(res => res.data);
}

// 删除电影评论
export function deleteComment(commentId) {
    return axios.delete(`/api/comments/${commentId}`).then(res => res.data);
}
